import React from 'react';
import '../styles.css';
// import 'bootstrap/dist/css/bootstrap.min.css';
import NavBar from '../components/NavBar'
//import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav';
import { Link, Route, Switch, Redirect, useLocation } from 'react-router-dom';

import Home  from '../views/Home'
import About from '../views/About'
import Reading from './Reading'
import Portfolio from '../views/Portfolio'
import { Container } from 'react-bootstrap';

const sections = [
  {title: 'Home', route:'/home', component: <Home />}, 
  {title: 'About', route:'/about', component: <About />}, 
  {title: 'Reading', route:'/reading', component: <Reading />}, 
  {title: 'Portfolio', route:'/portfolio', component: <Portfolio />},
]

function App() {

  // constructor(props) {
  //   super(props);
  //   this.state = {
  //     current: 'Home'
  //   }
  // }

    return (
      <Container className="App">
        {console.log(useLocation())}
        {this.state.current !== 'Home' ? <NavBar sections={sections} /> : null}
        <div className="Body">
            <Switch>
              <Redirect from="/" exact to="/home" />
              {sections.map((section) => (
                <Route exact path={section.route}>
                  {section.component}
                </Route>
              ))}
            </Switch>
        </div>
        {/* <Footer /> */}
      </Container>
    );
}

export default App;
